var express = require('express');

var PORT = process.env.PORT || 3001;
var app = express();

var routes = require('./routes');

var mongoose = require('mongoose');

mongoose.set('useCreateIndex', true); // Define Middleware Here -------------------------------------

app.use(express.urlencoded({
  extended: true
}));
app.use(express.json()); // Serve Up Static Assets (usually on Heroku) -----------------

if (process.env.NODE_ENV === 'production') {
  app.use(express.static('client/build'));
} // Define API Routes Here -------------------------------------


app.use(routes); // Connect to the MongoDB Database ----------------------------

mongoose.connect(process.env.MONGODB_URI || "mongodb://localhost/googlebooks", {
  useNewUrlParser: true
});
app.listen(PORT, function () {
  console.log("API Server now on port: ".concat(PORT));
});
