var mongoose = require('mongoose');

var Schema = mongoose.Schema;
var BookSchema = new Schema({
  title: {
    type: String,
    required: true
  },
  authors: {
    type: [String],
    required: true
  },
  description: {
    type: String,
    required: true
  },
  googleBookId: {
    type: String,
    required: true
  },
  thumbnail: String,
  link: {
    type: String,
    required: true
  },
  pageCount: String,
  subtitle: String,
  publishedDate: String,
  date: {
    type: Date,
    default: Date.now
  }
});
var Book = mongoose.model('Book', BookSchema);
module.exports = Book;
